import axios from 'axios'
import { Comment, Avatar, Button, Input } from 'antd';
import React, { useContext,useState , useEffect } from 'react'
import { UserContext } from '../../../context/UserProvider.js'
import LikeDislikes from './LikeDislikes.js';
export default function SingleComment(props){
    const actions = [
        <LikeDislikes comment={props.comment} commentId={props.comment._id} userId={localStorage.getItem('userId')}/>,
        <span onClick key="comment-basic-reply-to">Reply to </span>
    ]
    return (
        <div>
            <p><span style>{props.comment.writer.username}</span>: {props.comment.content}</p>
            <span onClick key="comment-basic-reply-to">Reply to </span> 
            <LikeDislikes comment={props.comment} commentId={props.comment._id} userId={localStorage.getItem('userId')}/>
        </div>
    )
}
