import React, { useEffect, useState } from 'react'
import { LikeFilled, LikeOutlined, DislikeFilled, DislikeOutlined } from '@ant-design/icons'
import { Tooltip, Icon} from 'antd';
import axios from 'axios';
const userAxios = axios.create()

userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem('token')
    config.headers.Authorization = `Bearer ${token}`
    return config
})

export default function LikeDislikes(props) {
    const [Likes, setLikes] = useState(0)
    const [Dislikes, setDislikes] = useState(0)
    const [LikeAction, setLikeAction] = useState(null)
    const [DislikeAction, setDislikeAction] = useState(null)

    let variable = { issueId: props.issueId, userId: props.userId}

    useEffect(() => {
        userAxios.post('/api/like/getLikes', variable)
            .then(res => {
                console.log('getLikes', res.data)
                if (res.data.success){
                    setLikes(res.data.likes.length)

                    res.data.likes.map(like => {
                        if (like.userId === props.userId) {
                            setLikeAction('liked')
                        }
                    })
                } else {
                    alert('Failed to get likes')
                }
            })
        userAxios.post('/api/like/getDislikes', variable)
            .then(response => {
                console.log('getDislike',response.data)
                if (response.data.success) {
                    //How many likes does this video or comment have 
                    setDislikes(response.data.dislikes.length)

                    //if I already click this like button or not 
                    response.data.dislikes.map(dislike => {
                        if (dislike.userId === props.userId) {
                            setDislikeAction('disliked')
                        }
                    })
                } else {
                    alert('Failed to get dislikes')
                }
        })
    }, [])

    return (
        <>
            <span>
                <Tooltip>
                    <LikeFilled type='Like'></LikeFilled>
                </Tooltip>
            </span>&nbsp;&nbsp;
            <span>dislike</span>
        </>
    )
}
