import React, {useEffect, useState} from 'react'
import Issue from './Issue.js'

export default function IssueList(props){
    const { issues, deleteIssue, likeIssue, userId } = props
    
    const [data, setData] = useState(0);
    const [sortType, setSortType] = useState('likes');
    useEffect(() => {
        const sortArray = type => {
          const types = {
            _id: 'albums',
            title: 'members',
            description: 'formed_in',
            likes: 0
          };
          const sortProperty = types[type];
          const sorted = [...issues].sort((a, b) => b[sortProperty] - a[sortProperty]);
          setData(sorted);
        };
    
        sortArray(sortType);
      }, [sortType]);
      console.log(issues)
    return (
        <div className='issue-list'>
            { issues.map(issue => <Issue {...issue} userId={userId} likeIssue={likeIssue} deleteIssue={deleteIssue} id={issue._id} key={issue._id}/>)}
        </div>
    )
}
